var authurl = pm.environment.get("auth_url");
var realm = pm.environment.get("realm");
var clientid = pm.environment.get("clientid");
var userid = pm.environment.get("userid");
var password = pm.environment.get("password");
var client_secret = pm.environment.get("client_secret");

const echoPostRequest = {
  url: authurl +'/auth/realms/' +realm + '/protocol/openid-connect/token',
  method: 'POST',
  header: 'Content-Type:application/x-www-form-urlencoded',
  body: {
    mode: 'raw',
    raw: 'grant_type=password&client_id=' 
         +clientid 
         +'&username=' 
         +userid 
         +'&password='
         +password
         +'&client_secret='
         +client_secret
  }
};
pm.sendRequest(echoPostRequest, function (err, res) {
    if (err) { console.log(err); }
    else{
      var jsonData = res.json();
      pm.environment.set("token", jsonData.access_token);
      pm.environment.set("refresh_token", jsonData.refresh_token);
      pm.environment.set("token_expires", Date.now()+(jsonData.expires_in * 1000));
      pm.environment.set("refresh_token_expires", Date.now()+(jsonData.refresh_expires_in * 1000));
    }
    //console.log(err ? err : res.json());
});

var authurl = pm.environment.get("auth_url");
var realm = pm.environment.get("realm");
var clientid = pm.environment.get("clientid");
var refresh_token = pm.environment.get("refresh_token");
var client_secret = pm.environment.get("client_secret");

const echoPostRequest = {
  url: authurl +'/auth/realms/' +realm + '/protocol/openid-connect/token',
  method: 'POST',
  header: 'Content-Type:application/x-www-form-urlencoded',
  body: {
    mode: 'raw',
    raw: 'grant_type=refresh_token&client_id=' 
         +clientid 
         +'&refresh_token=' 
         +refresh_token 
         +'&client_secret='
         +client_secret
  }
};
pm.sendRequest(echoPostRequest, function (err, res) {
    var jsonData = res.json();
    pm.environment.set("token", jsonData.access_token);
    pm.environment.set("refresh_token", jsonData.refresh_token);
    pm.environment.set("token_expires", Date.now()+(jsonData.expires_in * 1000));
    pm.environment.set("refresh_token_expires", Date.now()+(jsonData.refresh_expires_in * 1000));

    console.log(err ? err : res.json());
});

